import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'motion/react';
import Header from './components/Header';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';

type Todo = {
  id: string;
  text: string;
  completed: boolean;
};

const STORAGE_KEY = 'todos';

const App: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);

  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      setTodos(JSON.parse(saved));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(todos));
  }, [todos]);

  const addTodo = (text: string) => {
    setTodos([...todos, { id: Date.now().toString(), text, completed: false }]);
  };

  const toggleTodo = (id: string) => {
    setTodos(todos.map(todo => 
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    ));
  };

  const deleteTodo = (id: string) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 p-4 flex items-center justify-center font-sans">
      <div className="w-full max-w-md bg-white/10 backdrop-blur-lg rounded-2xl shadow-xl p-6 border border-white/20">
        <Header />
        <TodoForm addTodo={addTodo} />
        <TodoList 
          todos={todos} 
          toggleTodo={toggleTodo} 
          deleteTodo={deleteTodo} 
        />
      </div>
    </div>
  );
};

export default App;

import React from 'react';
import { motion } from 'motion/react';

const Header: React.FC = () => {
  return (
    <motion.h1 
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      className="text-3xl font-bold text-white text-center mb-6"
    >
      Todo App
    </motion.h1>
  );
};

export default Header;

import React, { useState } from 'react';
import { PlusCircle } from 'lucide-react';
import { motion } from 'motion/react';

interface TodoFormProps {
  addTodo: (text: string) => void;
}

const TodoForm: React.FC<TodoFormProps> = ({ addTodo }) => {
  const [text, setText] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (text.trim()) {
      addTodo(text.trim());
      setText('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mb-6">
      <div className="flex gap-2">
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Add a new task..."
          className="flex-1 px-4 py-3 rounded-xl bg-white/20 border border-white/30 text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/50"
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          type="submit"
          className="bg-white text-purple-600 p-3 rounded-xl flex items-center justify-center"
        >
          <PlusCircle size={24} />
        </motion.button>
      </div>
    </form>
  );
};

export default TodoForm;

import React from 'react';
import { AnimatePresence } from 'motion/react';
import TodoItem from './TodoItem';

interface Todo {
  id: string;
  text: string;
  completed: boolean;
}

interface TodoListProps {
  todos: Todo[];
  toggleTodo: (id: string) => void;
  deleteTodo: (id: string) => void;
}

const TodoList: React.FC<TodoListProps> = ({ todos, toggleTodo, deleteTodo }) => {
  if (todos.length === 0) {
    return (
      <p className="text-white/70 text-center py-4">No tasks yet. Add one above!</p>
    );
  }

  return (
    <ul className="space-y-3">
      <AnimatePresence>
        {todos.map(todo => (
          <TodoItem
            key={todo.id}
            todo={todo}
            toggleTodo={toggleTodo}
            deleteTodo={deleteTodo}
          />
        ))}
      </AnimatePresence>
    </ul>
  );
};

export default TodoList;

import React from 'react';
import { motion } from 'motion/react';
import { CheckCircle2, Circle, Trash2 } from 'lucide-react';

interface Todo {
  id: string;
  text: string;
  completed: boolean;
}

interface TodoItemProps {
  todo: Todo;
  toggleTodo: (id: string) => void;
  deleteTodo: (id: string) => void;
}

const TodoItem: React.FC<TodoItemProps> = ({ todo, toggleTodo, deleteTodo }) => {
  return (
    <motion.li
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, x: -50 }}
      className="flex items-center gap-3 p-3 bg-white/10 backdrop-blur rounded-xl border border-white/20"
    >
      <button 
        onClick={() => toggleTodo(todo.id)}
        className="text-white hover:text-yellow-300 transition-colors"
      >
        {todo.completed ? <CheckCircle2 size={24} /> : <Circle size={24} />}
      </button>
      <span 
        className={`flex-1 text-white ${todo.completed ? 'line-through opacity-70' : ''}`}
      >
        {todo.text}
      </span>
      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={() => deleteTodo(todo.id)}
        className="text-red-400 hover:text-red-300 transition-colors"
      >
        <Trash2 size={20} />
      </motion.button>
    </motion.li>
  );
};

export default TodoItem;

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { PlusCircle } from 'lucide-react';

function App() {
  const [count, setCount] = useState(0);

  const incrementCount = () => {
    setCount(prevCount => prevCount + 1);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 text-white flex items-center justify-center p-4 font-inter">
      <motion.div
        className="bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-lg rounded-3xl shadow-xl border border-white/20 p-8 w-full max-w-sm mx-auto flex flex-col items-center space-y-6"
        initial={{ opacity: 0, y: 50, scale: 0.9 }}
        animate={{ opacity: 1, y: 0, scale: 1 }}
        transition={{ duration: 0.6, ease: 'easeOut' }}
      >
        <motion.h1
          className="text-5xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-purple-300 to-blue-300 drop-shadow-lg"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.5 }}
        >
          Counter App
        </motion.h1>

        <motion.p
          className="text-8xl font-bold text-white tracking-wider font-space-grotesk"
          key={count} // Key change forces re-animation
          initial={{ scale: 0.5, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ type: 'spring', stiffness: 200, damping: 15 }}
        >
          {count}
        </motion.p>

        <motion.button
          onClick={incrementCount}
          className="relative overflow-hidden px-8 py-3 rounded-full bg-gradient-to-r from-purple-600 to-blue-600 text-lg font-semibold text-white shadow-lg
                     hover:from-purple-700 hover:to-blue-700 transition-all duration-300 ease-in-out
                     focus:outline-none focus:ring-4 focus:ring-purple-300 focus:ring-opacity-70 flex items-center gap-2 group"
          whileTap={{ scale: 0.95 }}
          whileHover={{ scale: 1.05 }}
        >
          <PlusCircle className="relative z-10 w-5 h-5 group-hover:rotate-90 transition-transform duration-300" />
          <span className="relative z-10">Increment Count</span>
          <div className="absolute inset-0 bg-gradient-to-r from-purple-700 to-blue-700 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
        </motion.button>
      </motion.div>
    </div>
  );
}

export default App;

import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'motion/react';
import { ListTodo, Plus, Search, Calendar as CalendarIcon, Filter } from 'lucide-react';
import TodoItem from './components/TodoItem';
import TodoInput from './components/TodoInput';
import Stats from './components/Stats';

export type Todo = {
  id: string;
  text: string;
  completed: boolean;
  createdAt: number;
};

type FilterType = 'all' | 'active' | 'completed';

export default function App() {
  const [todos, setTodos] = useState<Todo[]>(() => {
    const saved = localStorage.getItem('techwiser-todos');
    return saved ? JSON.parse(saved) : [];
  });
  const [filter, setFilter] = useState<FilterType>('all');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    localStorage.setItem('techwiser-todos', JSON.stringify(todos));
  }, [todos]);

  const addTodo = (text: string) => {
    const newTodo: Todo = {
      id: crypto.randomUUID(),
      text,
      completed: false,
      createdAt: Date.now(),
    };
    setTodos([newTodo, ...todos]);
  };

  const toggleTodo = (id: string) => {
    setTodos(todos.map(t => t.id === id ? { ...t, completed: !t.completed } : t));
  };

  const deleteTodo = (id: string) => {
    setTodos(todos.filter(t => t.id !== id));
  };

  const clearCompleted = () => {
    setTodos(todos.filter(t => !t.completed));
  };

  const filteredTodos = todos
    .filter(todo => {
      if (filter === 'active') return !todo.completed;
      if (filter === 'completed') return todo.completed;
      return true;
    })
    .filter(todo => todo.text.toLowerCase().includes(searchQuery.toLowerCase()));

  return (
    <div className="min-h-screen bg-[#F8FAFC] text-slate-900 font-sans selection:bg-indigo-100 selection:text-indigo-700">
      <div className="max-w-2xl mx-auto px-4 py-12 md:py-20">
        {/* Header */}
        <header className="mb-10">
          <div className="flex items-center gap-3 mb-2">
            <div className="p-2 bg-indigo-600 rounded-xl shadow-lg shadow-indigo-200">
              <ListTodo className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-3xl font-bold tracking-tight text-slate-900 italic">FlowTask</h1>
          </div>
          <p className="text-slate-500 font-medium">Organize your day with clarity and precision.</p>
        </header>

        <Stats todos={todos} />

        <div className="space-y-6">
          <div className="flex flex-col gap-4">
            <TodoInput onAdd={addTodo} />

            <div className="flex items-center gap-2 overflow-x-auto pb-2 scrollbar-hide">
               <div className="relative flex-1 min-w-[200px]">
                  <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
                  <input 
                    type="text"
                    placeholder="Search tasks..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full pl-10 pr-4 py-2 bg-white border border-slate-200 rounded-xl focus:outline-hidden focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 transition-all text-sm"
                  />
               </div>
               <div className="flex bg-white p-1 rounded-xl border border-slate-200">
                  {(['all', 'active', 'completed'] as FilterType[]).map((f) => (
                    <button
                      key={f}
                      onClick={() => setFilter(f)}
                      className={`px-4 py-1.5 rounded-lg text-sm font-medium capitalize transition-all ${
                        filter === f 
                          ? 'bg-indigo-50 text-indigo-600' 
                          : 'text-slate-500 hover:bg-slate-50'
                      }`}
                    >
                      {f}
                    </button>
                  ))}
               </div>
            </div>
          </div>

          <div className="space-y-3">
            <AnimatePresence mode='popLayout'>
              {filteredTodos.map((todo) => (
                <TodoItem 
                  key={todo.id} 
                  todo={todo} 
                  onToggle={toggleTodo} 
                  onDelete={deleteTodo} 
                />
              ))}
            </AnimatePresence>

            {filteredTodos.length === 0 && (
              <motion.div 
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="text-center py-20 bg-white/50 border border-dashed border-slate-300 rounded-3xl"
              >
                <div className="mx-auto w-12 h-12 bg-slate-100 rounded-full flex items-center justify-center mb-4">
                   <Filter className="w-6 h-6 text-slate-400" />
                </div>
                <h3 className="text-slate-900 font-semibold">No tasks found</h3>
                <p className="text-slate-500 text-sm">Try adjusting your search or filter.</p>
              </motion.div>
            )}
          </div>

          {todos.some(t => t.completed) && (
            <div className="flex justify-end">
               <button 
                onClick={clearCompleted}
                className="text-sm text-slate-400 hover:text-red-500 font-medium transition-colors"
               >
                 Clear completed tasks
               </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

import React from 'react';
import { motion } from 'motion/react';
import { CheckCircle2, Circle, Trash2 } from 'lucide-react';
import { Todo } from '../App';

interface Props {
  todo: Todo;
  onToggle: (id: string) => void;
  onDelete: (id: string) => void;
}

export default function TodoItem({ todo, onToggle, onDelete }: Props) {
  return (
    <motion.div
      layout
      initial={{ opacity: 0, scale: 0.95, y: 10 }}
      animate={{ opacity: 1, scale: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.95, y: -10 }}
      className={`group flex items-center gap-4 p-4 bg-white border rounded-2xl transition-all shadow-xs hover:shadow-md ${
        todo.completed ? 'border-slate-100 opacity-75' : 'border-slate-200'
      }`}
    >
      <button
        onClick={() => onToggle(todo.id)}
        className={`transition-colors ${
          todo.completed ? 'text-indigo-600' : 'text-slate-300 hover:text-indigo-400'
        }`}
      >
        {todo.completed ? <CheckCircle2 className="w-6 h-6 shrink-0" /> : <Circle className="w-6 h-6 shrink-0" />}
      </button>

      <div className="flex-1 min-w-0">
        <p className={`text-[15px] font-medium transition-all truncate ${
          todo.completed ? 'text-slate-400 line-through' : 'text-slate-700'
        }`}>
          {todo.text}
        </p>
        <p className="text-[11px] text-slate-400 mt-0.5">
          {new Date(todo.createdAt).toLocaleDateString(undefined, { 
            month: 'short', 
            day: 'numeric', 
            hour: '2-digit', 
            minute: '2-digit' 
          })}
        </p>
      </div>

      <button
        onClick={() => onDelete(todo.id)}
        className="opacity-0 group-hover:opacity-100 p-2 text-slate-300 hover:text-red-500 hover:bg-red-50 rounded-lg transition-all"
      >
        <Trash2 className="w-4 h-4" />
      </button>
    </motion.div>
  );
}

import React, { useState } from 'react';
import { Plus } from 'lucide-react';

interface Props {
  onAdd: (text: string) => void;
}

export default function TodoInput({ onAdd }: Props) {
  const [text, setText] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (text.trim()) {
      onAdd(text.trim());
      setText('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="relative group">
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Add a new task..."
        className="w-full pl-6 pr-14 py-4 bg-white border-2 border-slate-100 rounded-2xl focus:outline-hidden focus:border-indigo-500 transition-all text-slate-700 font-medium placeholder:text-slate-400 shadow-sm focus:shadow-indigo-100"
      />
      <button
        type="submit"
        disabled={!text.trim()}
        className="absolute right-3 top-1/2 -translate-y-1/2 p-2 bg-indigo-600 text-white rounded-xl hover:bg-indigo-700 disabled:opacity-40 disabled:hover:bg-indigo-600 transition-all shadow-lg shadow-indigo-100 active:scale-95"
      >
        <Plus className="w-5 h-5" />
      </button>
    </form>
  );
}

import React from 'react';
import { Todo } from '../App';

interface Props {
  todos: Todo[];
}

export default function Stats({ todos }: Props) {
  const completed = todos.filter(t => t.completed).length;
  const total = todos.length;
  const progress = total === 0 ? 0 : Math.round((completed / total) * 100);

  return (
    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
      <div className="bg-indigo-600 p-4 rounded-3xl text-white shadow-xl shadow-indigo-100">
        <p className="text-indigo-100 text-xs font-semibold uppercase tracking-wider mb-1">Progress</p>
        <p className="text-2xl font-bold">{progress}%</p>
        <div className="mt-2 w-full bg-indigo-400/30 h-1.5 rounded-full overflow-hidden">
          <div 
            className="bg-white h-full transition-all duration-500 ease-out"
            style={{ width: `${progress}%` }}
          />
        </div>
      </div>

      <div className="bg-white p-4 rounded-3xl border border-slate-200 shadow-xs">
        <p className="text-slate-400 text-xs font-semibold uppercase tracking-wider mb-1">Active</p>
        <p className="text-2xl font-bold text-slate-800">{total - completed}</p>
      </div>

      <div className="bg-white p-4 rounded-3xl border border-slate-200 shadow-xs">
        <p className="text-slate-400 text-xs font-semibold uppercase tracking-wider mb-1">Done</p>
        <p className="text-2xl font-bold text-slate-800">{completed}</p>
      </div>

      <div className="hidden md:block bg-white p-4 rounded-3xl border border-slate-200 shadow-xs">
        <p className="text-slate-400 text-xs font-semibold uppercase tracking-wider mb-1">Total Tasks</p>
        <p className="text-2xl font-bold text-slate-800">{total}</p>
      </div>
    </div>
  );
}
